document.addEventListener("DOMContentLoaded",()=>{let e=document.getElementById("theme"),t=document.documentElement,s=localStorage.getItem("theme");s&&t.classList.add(s),e.addEventListener("click",()=>{t.classList.contains("dark")?(t.classList.remove("dark"),localStorage.setItem("theme","light")):(t.classList.add("dark"),localStorage.setItem("theme","dark"))})});