function toggleTheme(){const e=document.getElementById('toggleButton');const t=document.getElementById('toggleSwitch');const n=document.documentElement;e.classList.toggle('translate-x-5');t.classList.toggle('bg-blue-700');n.classList.toggle('dark');localStorage.setItem('theme',n.classList.contains('dark')?'dark':'light');localStorage.setItem('toggleState',e.classList.contains('translate-x-5')?'on':'off')}document.addEventListener('DOMContentLoaded',function(){const e=document.getElementById('toggleButton');const t=document.documentElement;const n=localStorage.getItem('theme');const a=localStorage.getItem('toggleState');'dark'===n&&t.classList.add('dark');'on'===a&&e.classList.add('translate-x-5')});